function App() {
  return (
    <div className="App">
      <Map/>
    </div>
  );
}
function renderHTML() {
  console.log('Rendering HTML:')
  console.log(Page.substring(Page.length - 7))
  console.log(Page.substring(0, 6))
  // return Page
  return Page.substring(7, Page.length - 8)
}
const scopes = {
  COUNTIES: 'counties',
  STATES: 'states',
  GLOBAL: 'global'
}
class Map extends React.Component {
  constructor(props) {
    super(props)
    this.state = {scope: scopes.GLOBAL}
    this.switchMap = this.switchMap.bind(this)
    this.choose = this.choose.bind(this)
  }
  render() {
    return(
      <div>
        <button onClick={() => {
          this.state.scope = scopes.STATES
        }}>
          US States
        </button>
        <button onClick={() => {
          this.state.scope = scopes.GLOBAL
        }}>
          World
        </button>
        { this.choose() }
      </div>
    );
  }
  choose() {
    switch(this.state.scope) {
            case scopes.COUNTIES:
              return null;
            case scopes.STATES:
              return this.renderMap(states_json)
            case scopes.GLOBAL:
              return this.renderMap(countries_json)
          }
  }
  switchMap() {
    this.setState({countriesVisible: !this.state.countriesVisible, statesVisible: !this.state.statesVisible})
    console.log('Done ', this.state.statesVisible);
  }

  renderMap(json) {
    console.log(json)
    console.log(json.data)
    // var figure = JSON.parse(json)
    //var plot = Plotly.newPlot('graph-div', figure.data, figure.layout);
    return (
        <Plot
          data={json.data}
          layout={json.layout}
          frames={json.frames}
        />
      )
  }

}

export default App;
